import './css/signUpEmployee.css'

export const SignUpEmployee = () =>{

    const validateName=(e)=>{
        let signup_username= document.getElementById('signupEmployee_username')
        let char=e.target.value;
        char=char[char.length-1]
        if(!((char>='a' && char<='z') ||(char>='A' && char<='Z')|| (char>='0' && char<='9') || char==='@' || char==='$' || char==='_')){
            signup_username.value=signup_username.value.slice(0, signup_username.value.length-1);
            document.getElementById('username_label').style.animation='error_animation-animation 1s ease-in-out 0s'
            setTimeout(()=>{
                document.getElementById('username_label').style.animation=''
            },1000)
        }
    }

    return(
        <div className="signUpEmployee_container_one">
            <form action='#'>
                <div className="signUpEmployee_container_two">
                    <div style={{display:'flex', flexDirection:'column', margin:"10px 0px", height:"70px"}}>
                        <div><label className='signUpEmployee_container_two_label' id='username_label'>Username</label><span className='signUpEmployee_container_two_label_errormessage' id='signUpEmployee_container_two_label_one'>  *Use only (@,$,_) in your name</span></div>
                        <input type="text" className='signupEmployee_container_two_input' placeholder='***** Enter name you want to register *****' id="signupEmployee_username" required onChange={validateName}/>
                    </div>
                </div>
            </form>
        </div>
    )
}
