import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Imag = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get('http://localhost:9001/images/all');
        const imageList = response.data.map(image => ({
          ...image,
          data1: image.data1 ? `data:image/png;base64,${btoa(String.fromCharCode(...new Uint8Array(image.data1)))}` : '',
          data2: image.data2 ? `data:image/png;base64,${btoa(String.fromCharCode(...new Uint8Array(image.data2)))}` : '',
          data3: image.data3 ? `data:image/png;base64,${btoa(String.fromCharCode(...new Uint8Array(image.data3)))}` : '',
          data4: image.data4 ? `data:image/png;base64,${btoa(String.fromCharCode(...new Uint8Array(image.data4)))}` : '',
          data5: image.data5 ? `data:image/png;base64,${btoa(String.fromCharCode(...new Uint8Array(image.data5)))}` : '',
        }));
        setImages(imageList);
      } catch (err) {
        console.error('Error fetching images:', err);
        setError('Error fetching images: ' + err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchImages();
  }, []);

  return (
    <div>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      <div className="image-gallery">
        {images.map((image) => (
          <div key={image.id}>
            <h3>{image.name}</h3>
            {image.data1 && <img src={image.data1} alt={`${image.name} - Image 1`} />}
            {image.data2 && <img src={image.data2} alt={`${image.name} - Image 2`} />}
            {image.data3 && <img src={image.data3} alt={`${image.name} - Image 3`} />}
            {image.data4 && <img src={image.data4} alt={`${image.name} - Image 4`} />}
            {image.data5 && <img src={image.data5} alt={`${image.name} - Image 5`} />}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageDisplay;
