import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ImageDisplay = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get('http://localhost:9001/images/');
        setImages(response.data);
      } catch (err) {
        setError('Error fetching images');
      } finally {
        setLoading(false);
      }
    };

    fetchImages();
  }, []);

  if (loading) {
    return <div>Loading images...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="image-gallery">
      {images.length > 0 ? (
        images.map((image) => (
          <div key={image.id} className="image-item">
            <h3>{image.name}</h3>
            <p>Cost: {image.cost}</p>
            <p>Material Used: {image.materialused}</p>
            <p>Description: {image.description}</p>
            <img
              src={`data:image/png;base64,${btoa(String.fromCharCode(...new Uint8Array(image.data1)))}`}
              alt={image.name}
              style={{ width: '100%', height: 'auto' }}
            />
            {/* Repeat for other image data fields as needed */}
          </div>
        ))
      ) : (
        <div>No images found</div>
      )}
    </div>
  );
};

export default ImageDisplay;
