import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ImageDisplay = () => {
    const [images, setImages] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchImages = async () => {
            try {
                const response = await axios.get('http://localhost:9001/images'); // Adjust the URL if needed
                // Ensure response data structure matches what you're trying to access
                const base64Images = response.data.map((img) => ({
                    id: img.id,
                    data1: `data:image/png;base64,${Buffer.from(img.data1).toString('base64')}`, // Ensure this is correct
                    data2: `data:image/png;base64,${Buffer.from(img.data2).toString('base64')}`,
                    data3: `data:image/png;base64,${Buffer.from(img.data3).toString('base64')}`,
                    data4: `data:image/png;base64,${Buffer.from(img.data4).toString('base64')}`,
                    data5: `data:image/png;base64,${Buffer.from(img.data5).toString('base64')}`,
                    name: img.name,
                }));
                setImages(base64Images);
            } catch (err) {
                setError('Error fetching images');
                console.error(err); // Log the error for debugging
            } finally {
                setLoading(false);
            }
        };

        fetchImages();
    }, []);

    if (loading) return <div>Loading images...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div>
            <h1>Image Gallery</h1>
            <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                {images.map((image) => (
                    <div key={image.id} style={{ margin: '10px' }}>
                        <h2>{image.name}</h2>
                        <img
                            src={image.data1} // Change to whichever image you want to display
                            alt={image.name}
                            style={{ width: '150px', height: '150px', objectFit: 'cover' }}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ImageDisplay;
