        //                     <img src={require('../images/about.png')} className='buyDress_dress_holder_image' alt='test'/>
        //                     <div className='buydress_dress_holder_desc'>
        //                         <p>Name</p>
        //                         <p>Size</p>
        //                         <p>Seller</p><br></br>
        //                         <p>Cost</p>
        //                     </div>
        //                     <p style={{alignSelf:"start", paddingTop:"10px"}}>Delivered by</p>
        //                 </div>
